USE gold_db;
GO

CREATE OR ALTER PROC CreateSQLServerlessView_gold 
    @ViewName NVARCHAR(100)
AS 
BEGIN
    DECLARE @statement NVARCHAR(MAX);

    SET @statement = 
        N'CREATE OR ALTER VIEW ' + QUOTENAME(@ViewName) + ' AS
        SELECT *
        FROM
            OPENROWSET(
                BULK ''covid2025storage.dfs.core.windows.net/gold/' + @ViewName + '.csv'',
                FORMAT = ''CSV'',
                PARSER_VERSION = ''2.0'',
                HEADER_ROW = TRUE
            ) AS [result];';

    EXEC sp_executesql @statement;
END;
GO
