USE gold_db;
GO

CREATE OR ALTER PROC CreateSQLServerlessView_gold 
    @ViewName NVARCHAR(100)
AS 
BEGIN
    DECLARE @statement NVARCHAR(MAX);

    SET @statement = 
        N'CREATE OR ALTER VIEW ' + QUOTENAME(REPLACE(@ViewName, '.csv', '')) + ' AS
        SELECT *
        FROM
            OPENROWSET(
                BULK ''/' + @ViewName + ''',
                DATA_SOURCE = ''covid_data_source'',
                FORMAT = ''CSV'',
                PARSER_VERSION = ''2.0'',
                HEADER_ROW = TRUE
            ) AS [result];';

    EXEC sp_executesql @statement;
END;
GO
